import $ from 'jquery';
import axios from 'axios';
import { API_GET_ALL_USERS_CREATED, API_ACTIVATE_USER, HEADERS } from '../globalConfig/globalConstConfig';
export default {
  name: 'selectionnerUtilisateur',
  data() {
    return {
      errorMsg: null,
      search: '',
      headers: [{
        text: 'reference',
        value: 'id'
      }, {
        text: 'Nom',
        value: 'nom'
      }, {
        text: 'Prenoms',
        value: 'prenoms'
      }, {
        text: 'E-mail',
        value: 'email'
      }, {
        text: 'Login',
        value: 'login'
      }, {
        text: 'Actif',
        value: 'isActif'
      }, {
        text: 'Actions',
        value: 'actions',
        sortable: false
      }],
      loading: false,
      simpleObjectToSend: {
        data: {}
      },
      toActive: {
        data: {
          login: null
        }
      },
      usersList: []
    };
  },
  methods: {
    // ACTIVER UN UTILISATEUR
    async toActivedUser(userItem) {
      this.toActive.data.login = userItem.login;
      await axios.post(API_ACTIVATE_USER, this.toActive, {
        headers: HEADERS(this.$store.state.userAuthentified.token)
      }).then(response => {
        if (response.status == 200) {
          if (response.data.status.code != 800) {
            this.$swal.fire('Activation', response.data.status.message, 'error');
            this.toActive.data.login = null;
          } else {
            this.$swal.fire('Activation', response.data.status.message, 'success');
            this.toActive.data.login = null;
          }
        } else {
          this.$swal.fire('Activation', 'Error lors de la validation', 'error');
          this.toActive.data.login = null;
        }
      }).catch(e => {
        this.$swal.fire('Activation refusée', e, 'error');
        this.toActive.data.login = null;
      });
    },
    // RECUPERER LA LISTE DES UTILISATEURS CREES SUR LA PLATEFORME
    async toGetAllUsersCreated() {
      this.loading = true;
      await axios.post(API_GET_ALL_USERS_CREATED, this.simpleObjectToSend, {
        headers: HEADERS(this.$store.state.userAuthentified.token)
      }).then(response => {
        if (response.status == 200) {
          if (response.data.status.code != 800) {
            this.errorMsg = response.data.status.message;
            $(".alert-error").fadeIn();
            setTimeout(function () {
              $(".alert-error").fadeOut();
            }, 4000);
          } else {
            this.usersList = response.data.items;
          }
        } else {
          this.errorMsg = "Erreur";
          $(".alert-error").fadeIn();
          setTimeout(function () {
            $(".alert-error").fadeOut();
          }, 4000);
        }
      }).catch(e => {
        this.errorMsg = e;
      }).finally(() => {
        this.loading = false;
      });
    }
  },
  mounted() {
    this.toGetAllUsersCreated();
  }
};
